/**************************************************
** GAME BASE ENTITY
**************************************************/

var util = require("util");

var Entity = exports.Entity = function (options) {
	var self = this;

	// Default values
	self.id = null; // Id of entity (used to grab it from a list of entities)
	self.pos = null; // Position .x and .y
	self.vel = null; // Velocity .x and .y
	self.dimensions = null; // Size .width and .height
	self.remove = false; // Whether to kill and remove the entity
};

Entity.prototype.onBeforeInit = function() {};
Entity.prototype.onAfterInit = function() {};

Entity.prototype.worldUpdate = function(currentTime, timeDelta) {
	var self = this;

	self.move(currentTime, timeDelta);
	self.update(currentTime, timeDelta);
};

Entity.prototype.move = function(currentTime, timeDelta) {};
Entity.prototype.update = function(currentTime, timeDelta) {};

Entity.prototype.remove = function() {
	var self = this;
	self.remove = true;
};