/**************************************************
** EULER PHYSICS SIMULATOR
**************************************************/

var util = require("util");

var Euler = exports.Euler = {
	integrate: function(state, timeDelta) {
		state.pos.x += state.vel.x * timeDelta;
		state.pos.y += state.vel.y * timeDelta;

		var acceleration = {x: Math.cos(state.angle)*state.force, y: Math.sin(state.angle)*state.force};

		state.vel.x += acceleration.x * timeDelta;
		state.vel.y += acceleration.y * timeDelta;

		return state;
	}
};